CREATE TABLE `blog` (
  `blog_id` int(11) NOT NULL AUTO_INCREMENT,
  `user_id` int(11) DEFAULT NULL,
  `blog_name` varchar(50) NOT NULL,
  `summary` varchar(255) DEFAULT NULL,
  `categories` varchar(30) DEFAULT NULL,
  `blog_content` text,
  `date` date DEFAULT NULL,
  `likes` int(11) DEFAULT NULL,
  `img` varchar(50) CHARACTER SET utf8 COLLATE utf8_bin DEFAULT NULL,
  `state` int(11) DEFAULT NULL,
  PRIMARY KEY (`blog_id`),
  KEY `FK_Reference_3` (`user_id`),
  CONSTRAINT `FK_Reference_3` FOREIGN KEY (`user_id`) REFERENCES `user` (`user_id`)
) ENGINE=InnoDB AUTO_INCREMENT=44 DEFAULT CHARSET=utf8;

/*Data for the table `blog` */

insert  into `blog`(`blog_id`,`user_id`,`blog_name`,`summary`,`categories`,`blog_content`,`date`,`likes`,`img`,`state`) values (1,1,'1','1','1','1','2012-12-01',7,'1',1),(18,1,'说“振”道“震”','经常有人分不清“振”与“震”的区别，本文尝试从以下五个方面进行区分。','文化','<div style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#E5E7E7;\">\n	<b><span>一、从手与从雨</span></b>\n</div>\n<div style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#E5E7E7;\">\n	<span>“振”与“震”都是形声字，声旁都是“辰”。</span>\n</div>\n<div style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#E5E7E7;\">\n	<span>《说文解字》释“振”：“举救也。”“振”的本义是救济、赈济，出手相救，因此“振”的形旁是手（“扌”）。《广雅》则释“振”为“动也”，这是认为其本义是抖动、挥动，这个动作也与手有关，如“振臂一呼”。</span>\n</div>\n<div style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#E5E7E7;\">\n	<span>《说文解字》释“震”：“劈历，振物者。”“震”的本义是“劈历”（霹雳），是迅疾的雷，能够使物体动荡。雷和雨常常并作，因此“震”的形旁是雨。</span>\n</div>\n<div style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#E5E7E7;\">\n	<b><span>二、物理与心理</span></b>\n</div>\n<div style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#E5E7E7;\">\n	<span>这个角度，区分的是“振动”与“震动”。</span>\n</div>\n<div style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#E5E7E7;\">\n	<span>现代汉语中，“振动”是一个物理学词语，只能用于物理世界，指的是物体以某一空间位置为中点不断往复运动，如钟摆、音叉、琴弦的运动。而“震动”则既能用于物理世界，如“春雷震动着山谷”，也能用于心理世界，如“改革开放的巨大成果，震动了全世界”。</span>\n</div>\n<div style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#E5E7E7;\">\n	<i><span>留守儿童惨死的悲剧，（&emsp;&emsp;）了全国。</span></i>\n</div>\n<div style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#E5E7E7;\">\n	<span>上面括号中只能填“震动”，因为这个事件是在人们的内心世界产生了强烈的影响，掀起了巨大的情感波澜。</span>\n</div>\n<div style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#E5E7E7;\">\n	<span>要注意，“振动”与“震动”的这一区别在现代汉语中才形成。古汉语中，两者经常混用，因为当时尚未形成像今天这样的学科分类。具体例子就不列举了。</span>\n</div>\n<div style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#E5E7E7;\">\n	<b><span>三、有常与无常</span></b>\n</div>\n<div style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#E5E7E7;\">\n	<span>在物理世界中，“振动”与“震动”的运动形态是不同的。</span>\n</div>\n<div style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#E5E7E7;\">\n	<span>“振动”很多是有规律的，往往会延续比较长的时间，可以测量出振幅、周期等指标，因而是可以预测的，甚至可以有效利用，比如振动打桩机、振动压路机。</span>\n</div>\n<div style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#E5E7E7;\">\n	<span>而“震动”则很难把握，往往突然爆发，瞬间达到峰值，虽动能惊人，但难以预测，更不用说加以利用了，比如地震。</span>\n</div>\n<div style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#E5E7E7;\">\n	<span>简单说，“振动”有常，“震动”无常。</span>\n</div>\n<div style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#E5E7E7;\">\n	<i><span>（&emsp;&emsp;）性疾病，是由机械（&emsp;&emsp;）作用于人的全身或局部而引起的疾病。</span></i>\n</div>\n<div style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#E5E7E7;\">\n	<span>上面括号中只能填“振动”，这种疾病是由于振动源（振动车辆、振动工具等）强烈作用于人体而造成的。</span>\n</div>\n<div style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#E5E7E7;\">\n	<b><span>四、有利与不利</span></b>\n</div>\n<div style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#E5E7E7;\">\n	<span>在物理世界中，“振动性疾病”的例子说明，“振动工具”是于人有利的，但使用时也可能造成不利影响。还有些“振动”则完全是于人无利的，比如“船体振动学要研究船体的防振、测振和减振”。</span>\n</div>\n<div style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#E5E7E7;\">\n	<span>看到“减振”，有人问，难道不是“减震”吗？</span>\n</div>\n<div style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#E5E7E7;\">\n	<span>其实，不利于人的既有振动，也有震动，因此“减振”与“减震”都有。</span>\n</div>\n<div style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#E5E7E7;\">\n	<i><span>减振：使用手持振动工具时应注意采取减振措施。</span></i>\n</div>\n<div style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#E5E7E7;\">\n	<i><span>减震：超高建筑物要采取减震措施。</span></i>\n</div>\n<div style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#E5E7E7;\">\n	<span>《现代汉语规范词典》有“减震器”：“车辆悬挂装置或飞机起落架上的一种减震部件。有摩擦式和液压式两种。”而全国科学技术名词审定委员会公布的科技名词中，还有“减振器”。</span>\n</div>\n<div style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#E5E7E7;\">\n	<span>遗憾的是，科技名词委似乎把“减振器”与“减震器”的英语译名混为一谈了。比如，1998年公布的电气工程名词既有“减震器”又有“减振器”，其英文名都是vibration absorber。再比如，2003年公布的航空科学技术名词“减震器”（吸收飞机着陆时撞击动能，减少飞机滑跑时结构振动载荷的承载装置）和2013年公布的机械工程名词“减振器”（使振动衰减的装置），两者英文名都是shock absorber。其实，“震动”应用shock，“振动”则是vibration。</span>\n</div>\n<div style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#E5E7E7;\">\n	<b><span>五、积极与消极</span></b>\n</div>\n<div style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#E5E7E7;\">\n	<span>虽然现在“振动”与心理世界无关，但“振”和“震”都能产生心理影响。当然，它们的影响是不同的。</span>\n</div>\n<div style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#E5E7E7;\">\n	<span>“振”是有节奏地抖动、挥动，其结果往往是积极向上的，比如“振翅高飞、振作精神”。而“震”则是迅速而剧烈地动荡，其发生往往是突如其来的，给人带来的是压力甚至灾难，如“震惊、震慑”等。</span>\n</div>\n<div style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#E5E7E7;\">\n	<span>这一差别，在下面两词中表现明显。“振聋发聩”是一个褒义词，意思是发出很大的响声，使耳聋的人也能听到，比喻用语言文字唤醒糊涂的人。“震耳欲聋”也形容声音很大，但其效果却是负面的，耳朵都快震聋了，强调的是不适感。</span>\n</div>\n<div style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#E5E7E7;\">\n	<i><span>霹雳（&emsp;）耳</span></i>\n</div>\n<div style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#E5E7E7;\">\n	<span>上面括号中只能填“震”，因为这是人们不喜欢的，不能用“振”。</span>\n</div>\n<div style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#E5E7E7;\">\n	<span>但是，在这一点上，“振”与“震”的区别并不绝对。请看下面的例子：</span>\n</div>\n<div style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#E5E7E7;\">\n	<i><span>名（&emsp;）天下</span></i>\n</div>\n<div style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#E5E7E7;\">\n	<span>上面括号中“振”与“震”都能用，而且含义都是正面的。这是因为古汉语中，“振”与“震”常通用，“名震/振天下、名震/振四海、名震/振宇内”等说法古已有之，难分彼此。然而，如果翻译成白话，两者还是有区别的：“名震天下”可译为“声名震动天下”，“震”即“震动”；“名振天下”则应译为“声名扬于天下”，其中的“振”不是“振动”，而应理解为“兴起、显扬”。从结构上看，“名震天下”等于“名动天下”，“震天下”是动宾关系；而“名振天下”等于“名扬天下”，“振天下”是“振于天下”的缩略。</span>\n</div>\n<div style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#E5E7E7;\">\n	<span>“名声大震/振”，与此类似。古汉语中，两者难辨。但就今天的语感来看，“名声大振”的结构要比“名声大震”完整，因为“震”后面没有跟宾语，总让人感觉少了点什么。不知读者诸君以为然否。</span>\n</div>\n<div>\n	<span><br />\n</span>\n</div>','2018-12-30',36,NULL,0),(19,1,'北美崔哥：美国梦好实现，就是实在拿不出手','只要不是特别缺心眼儿的炎黄的孙子，在美国呆上几年，都能实现好几回美国梦。','娱乐','<h2 style=\"font-family:&quot;background-color:#BCD3E5;font-size:24px;color:#222222;\">\n	<strong>只要不是特别缺心眼儿的炎黄的孙子，在美国呆上几年，都能实现好几回美国梦。</strong>\n</h2>\n<p>\n	<p style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#000202;text-align:justify;\">\n		<span style=\"line-height:21px;color:#7B0C00;\"><strong><span style=\"line-height:21px;\"><span>什么是美国梦呢？</span></span></strong></span>\n	</p>\n	<p style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#000202;text-align:justify;\">\n		<span style=\"line-height:21px;\"><span><span style=\"line-height:27px;\">崔哥语录：就是普通中产白领的生活，有自己的房子（慢慢付，死前付完就成），有个不给钱坚决不干的工作或什么个破生意，年薪在</span><span style=\"line-height:normal;\">10</span><span style=\"line-height:27px;\">万</span><span style=\"line-height:normal;\">15</span><span style=\"line-height:27px;\">万美金以上，生一两个熊孩子，最好没什么教养那种好显得更美guo化。车库里有车，起码两辆，能第一下打着火就算。为了和美guo人看齐，最好周日去个教堂，家里养个带金毛的狗（能听得懂英语指令最好），假装看的懂橄榄球和垒球，假装喜欢篮球，背NBA球星跟乘法口诀似的；假装喜欢滑雪和打高尔夫，就这些了，这就是</span><span style=\"line-height:normal;\">American Dream&nbsp;</span><span style=\"line-height:27px;\">了。</span></span></span>\n	</p>\n	<p style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#000202;text-align:justify;\">\n		<span><br />\n</span>\n	</p>\n	<p style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#000202;text-align:justify;\">\n		<span style=\"line-height:21px;color:#7B0C00;\"><span>不是所有美国人都能实现这美guo梦，有好多英语特好的美国人，还没来得及买房子出国和吃一顿“大鸭梨”烤鸭就匆忙去世了。</span></span>\n	</p>\n	<p style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#000202;text-align:justify;\">\n		<span><br />\n</span>\n	</p>\n	<p style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#000202;text-align:justify;\">\n		<span style=\"line-height:21px;\"><span><span style=\"line-height:27px;\">北美崔哥的我</span><span style=\"line-height:normal;\">1988</span><span style=\"line-height:27px;\">年来的美国，</span><span style=\"line-height:normal;\">90</span><span style=\"line-height:27px;\">年至</span><span style=\"line-height:normal;\">92</span><span style=\"line-height:27px;\">年协助别人生了三个儿子（含双胞胎），</span><span style=\"line-height:normal;\">93</span><span style=\"line-height:27px;\">年买了自己的房子（二手的，马桶冲完水要按好几下要不丫老流），后来开过餐馆武馆等十几个有今儿没明儿的小生意，美国人见了我都特别羡慕，说没想到我这么快就实现美Guo梦了。后来也不知从哪天起，突然一下我成了“北美崔哥”，步入了名人的行列。虽然没挣到名人和明星的钱，但是被上百万人误认为是明星的感觉特别美好。现在比较怕见老街坊，怕被人认出来说 “就他呀”。</span></span></span>\n	</p>\n	<h2 style=\"font-family:&quot;background-color:#000202;font-weight:400;color:#333333;\">\n		<span style=\"line-height:31.5px;color:#7B0C00;\"><span>回到伟大祖国，时刻提醒自己千万别显呗，因为只有我心里明白，在今天的国内，我这点儿美Guo梦是拿不出手的。</span></span>\n	</h2>\n	<div style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#000202;\">\n		<p>\n			&nbsp;\n		</p>\n	</div>\n	<div style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#000202;\">\n		<p style=\"color:#333333;\">\n			<strong><span style=\"line-height:27px;\"><span style=\"line-height:27px;\">比如，如果大声说崔哥我在美国有个</span><span style=\"line-height:normal;\">50</span><span style=\"line-height:27px;\">来万美金的房子</span></span></strong><span style=\"line-height:27px;font-size:18px;\"><span style=\"line-height:27px;\">（合人民币</span><span style=\"line-height:normal;\">350</span><span style=\"line-height:27px;\">万元），我国内同学立刻说他在北京有三套，每套都价值三千多万，得，我立刻闭嘴了。</span></span>\n		</p>\n		<p style=\"color:#333333;\">\n			<span><br />\n</span>\n		</p>\n		<p style=\"color:#333333;\">\n			<span><strong><span style=\"line-height:27px;\">如果说崔哥我在美国开丰田凯美瑞和福特皮卡</span></strong><span style=\"line-height:27px;\">，一位通州区的小网红立刻让我看她白色的特斯拉，门能像小燕儿飞似的打开那种。</span></span>\n		</p>\n		<p style=\"color:#333333;\">\n			<span><br />\n</span>\n		</p>\n		<p style=\"color:#333333;\">\n			<span><strong><span style=\"line-height:27px;\">如果说崔哥我在美国开餐馆武馆旅行社和翻译公司</span></strong><span style=\"line-height:27px;\">，一河南哥们儿马上说他从事对冲基金风投和并购，再往下说我就听不懂了，而且还有点害怕，数额越来越大都快特么几百亿了。</span></span>\n		</p>\n		<p style=\"color:#333333;\">\n			<span><br />\n</span>\n		</p>\n		<p style=\"color:#333333;\">\n			<span><strong><span style=\"line-height:27px;\">如果说崔哥我在美国大小也是个名人有好几万粉丝</span></strong><span style=\"line-height:27px;\"><span style=\"line-height:27px;\">，某直播网红马上淡定地告诉我他在抖音有五百万粉)丝，他是</span><span style=\"line-height:normal;\">KOL</span><span style=\"line-height:27px;\">独立</span><span style=\"line-height:normal;\">IP</span><span style=\"line-height:27px;\">签了三家</span><span style=\"line-height:normal;\">MCN而且</span><span style=\"line-height:27px;\">已经快到</span><span style=\"line-height:normal;\">B</span><span style=\"line-height:27px;\">轮了，盘子有五到六个亿吧。我其实早就听不懂了，可是假装懂了不住地点头。</span></span></span>\n		</p>\n		<p style=\"color:#333333;\">\n			<span><br />\n</span>\n		</p>\n		<p style=\"color:#333333;\">\n			<span><strong><span style=\"line-height:27px;\">如果说崔哥我在美国有三个儿子</span></strong><span style=\"line-height:27px;\">，一景德镇老板立刻告诉我他的第三任老婆刚给他生了二胎，还教育我说生孩子是个苦差事，尽量别麻烦原配一个人生，不厚道。（我当时心里说你麻bi）</span></span>\n		</p>\n		<p style=\"color:#333333;\">\n			<span><br />\n</span>\n		</p>\n		<p style=\"color:#333333;\">\n			<span><strong><span style=\"line-height:27px;\">如果说崔哥我有美guo国籍可以去好多国家免签</span></strong><span style=\"line-height:27px;\"><span style=\"line-height:27px;\">，对面做美容的妹子马上亮出中国护照让我看上边</span><span style=\"line-height:normal;\">30</span><span style=\"line-height:27px;\">多个国家的签证，这姐姐连古巴赞比亚和萨拉热窝多都去过，说是去打干细胞。（那么穷的地儿有干细胞吗）</span></span></span>\n		</p>\n		<p style=\"color:#333333;\">\n			<span><br />\n</span>\n		</p>\n		<p style=\"color:#333333;\">\n			<span><strong><span style=\"line-height:27px;\">如果我说崔哥我在北京我天天住酒店</span></strong><span style=\"line-height:27px;\">，我发小儿立刻埋怨我说为什么不住他那里，北京的亮马河银泰和四季酒店都是他控股。他可以让我随便住，一分不花。我当时眼泪就下来了，后悔没有早找他。</span></span>\n		</p>\n		<p style=\"color:#333333;\">\n			<span><br />\n</span>\n		</p>\n		<p style=\"color:#333333;\">\n			<span><strong><span style=\"line-height:27px;\">如果跟人说崔哥我在美国演过电影</span></strong><span style=\"line-height:27px;\">，卖烤白薯的老哥马上告诉我他卖完这一炉马上就杀青了，下礼拜也出不了摊儿，因为要到横店作群众演员所以没有档期。</span></span>\n		</p>\n		<p style=\"color:#333333;\">\n			<span><br />\n</span>\n		</p>\n		<p style=\"color:#333333;\">\n			<span style=\"line-height:21px;color:#7B0C00;\"><span><span style=\"line-height:27px;\">总之，从美国回来的我，北美崔哥，感觉就是一窝囊废和</span><span style=\"line-height:normal;\">loser</span><span style=\"line-height:27px;\">（失败者），出国前是个每月挣</span><span style=\"line-height:normal;\">65</span><span style=\"line-height:27px;\">块人民币的穷人如今</span><span style=\"line-height:normal;\">30</span><span style=\"line-height:27px;\">年后回国了还是穷人一个。没机会炫耀，只能偷偷地光宗耀祖，一到特大饭局买单时就赶紧上厕所。</span></span></span>\n		</p>\n		<p style=\"color:#333333;\">\n			<span><br />\n</span>\n		</p>\n		<p style=\"color:#333333;\">\n			<span style=\"line-height:21px;\"><span>不错，我那美guo梦是实现了，可是总觉得真特么拿不出手呀。我现在都不知道埋怨谁好。埋怨美国？你丫真骗人，自由女神，你丫真是个大忽悠，说好的全世界都羡慕的美guo梦呢？</span></span>\n		</p>\n		<p style=\"color:#333333;\">\n			<span><br />\n</span>\n		</p>\n		<p style=\"color:#333333;\">\n			<span style=\"line-height:21px;\"><span>埋怨中国？你说你那么多年一穷二白都挺过来了，非崛起这么快干嘛，显你有能耐是吧。干嘛不等等海外姆们这帮精英呢，白喊你祖国母亲了。不够意思。</span></span>\n		</p>\n		<p style=\"color:#333333;\">\n			<span><br />\n</span>\n		</p>\n		<p style=\"color:#333333;\">\n			<span style=\"line-height:21px;\"><span>现在回国，最爱去的地方就是地铁或火车站，看到那么多人还不如自己，觉得特别励志，刚要得抑郁症一想算了吧，回头还得忍着疼找楼跳。</span></span>\n		</p>\n		<p style=\"color:#333333;\">\n			<span style=\"line-height:21px;\"><span><br />\n</span></span>\n		</p>\n		<p style=\"color:#333333;\">\n			<span style=\"line-height:21px;\"><span>再看看2018年，那么多比咱们有钱有名甚至还年轻的明星都走了，觉得自己和正在读这篇文章的您特别幸运，唉，今天能没灾没病地活着都算是人生赢家了。</span></span>\n		</p>\n	</div>\n</p>','2018-12-30',30,NULL,0),(27,1,'苹果的“倔强”促销 ','“尽管日子不好过，但面子还是不能丢。”这句话特适合这个冬天的苹果。','科技','<p align=\"justify\" style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#F8ECD8;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;日前，苹果官网上线了一项“折抵换购“的活动，消费者在购买新款iPhone XR、iPhone XS等机型时可以选择将自己闲置的智能手机来换取一定折扣，类似于传统汽车购买时的置换政策。不过，此次苹果的“置换”政策不仅仅只限iPhone置换，其他品牌的安卓机型也可以参与。\r\n</p>\r\n<p align=\"justify\" style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#F8ECD8;\">\r\n	另外，此次iPhone以旧换新的活动额度比此前有了很大提升，目前iPhone XR换购价4399元起，iPhone XS是6599元起，也就是说此次换购活动中用户的旧手机最多可以抵价达到2100元，这相比去年同期增长近50%。\r\n</p>\r\n<p align=\"justify\" style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#F8ECD8;\">\r\n	虽然苹果没有直接打出降价促销的标语，但这样的折抵换购活动其实某种意义上正是降价促销，苹果希望通过这样的方式来刺激新款iPhone的市场表现。\r\n</p>\r\n<p align=\"justify\" style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#F8ECD8;\">\r\n	<p align=\"justify\" style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#F8ECD8;\">\r\n		除了“折抵换购”之外，此前苹果还在用户更新系统时，通过弹窗的形式直接向用户推销过新的iPhone，鼓励老用户以旧换新。虽然弹窗推送可以更准确地将信息推送给目标用户群体，但这样的举动无疑会让用户感到反感，如此直白的推送在以往苹果的系统更新中是绝不可能出现的。\r\n	</p>\r\n	<p align=\"justify\" style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#F8ECD8;\">\r\n		除了硬件端的价格优惠之外，在软件层面的Apple Pay支付方面，苹果也在近日和上海地铁开展了新一轮优惠活动，目的自然是希望刺激用户对Apple Pay的使用，同时也试图通过增加互联网服务场景来提升用户粘性。\r\n	</p>\r\n	<p align=\"justify\" style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#F8ECD8;\">\r\n		一连串的动作背后，反映出的是新款iPhone市场表现的疲软，以及高库存带来的压力。\r\n	</p>\r\n	<p align=\"justify\" style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#F8ECD8;\">\r\n		根据第三方统计机构Counterpoint的最新数据显示，2018年第三季度，苹果在中国智能手机市场的份额从去年同期的10%下跌至9%，整体出货量更是下跌了17%。另外，新款iPhone并没有挽救苹果逐渐出现的疲软，而且由于市场对于其创新的失望，导致新款iPhone在全球市场范围内的表现均不理想。\r\n	</p>\r\n	<p align=\"justify\" style=\"color:#464646;font-family:&quot;font-size:14px;background-color:#F8ECD8;\">\r\n		其中，被苹果寄予厚望的“廉价版”iPhone XR似乎是重灾区。根据国外市场调研机构CIRP发布的最新调研数据显示，截止今年11月为止，新款iPhone XR的整体销量要低于去年同期的iPhone 8系列。具体数据方面，在11月单月iPhone XR的销量占了iPhone总销量的32%。而去年同期，iPhone8系列的销售占比则为39%。\r\n	</p>\r\n</p>','2018-12-30',35,NULL,0),(28,1,'崔永元称千亿矿权案卷疑丢失 ','最新消息：12月29日22:28，最高人民法院回应崔永元爆料：2018年12月29日13时32分，微博账号“崔永元”发博文并附四张图片。经核实，其中两张图片所载内容与目前保存在最高人民法院档案处的（2011）民一终字第81号案件副卷的有关内容相同（其他两张为媒体报道截图）。','新闻','<p style=\"font-size:16px;color:#333333;text-align:justify;font-family:arial;background-color:#FFFFFF;\">\r\n	<span style=\"color:#333333;font-family:arial;font-size:18px;font-weight:700;background-color:#FFFFFF;\">情况通报</span>\r\n</p>\r\n<p style=\"font-size:16px;color:#333333;text-align:justify;font-family:arial;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#333333;text-align:justify;font-family:arial;background-color:#FFFFFF;\">\r\n	就微博账号“崔永元”所发博文，最高人民法院微信公号12月29日晚发布情况通报称，已启动调查程序，欢迎崔永元教授等知情人向最高人民法院提供情况。“如发现我院工作人员违反审判纪律问题，将依纪依法严肃处理。”\r\n</p>\r\n<p style=\"font-size:16px;color:#333333;text-align:justify;font-family:arial;background-color:#FFFFFF;\">\r\n	<span class=\"bjh-p\"><span class=\"bjh-br\"></span></span>\r\n</p>\r\n<p style=\"font-size:16px;color:#333333;text-align:justify;font-family:arial;background-color:#FFFFFF;\">\r\n	<span class=\"bjh-p\">通报原文如下：</span>\r\n</p>\r\n<p style=\"font-size:16px;color:#333333;text-align:justify;font-family:arial;background-color:#FFFFFF;\">\r\n	<span class=\"bjh-p\"><span class=\"bjh-br\"></span></span>\r\n</p>\r\n<p style=\"font-size:16px;color:#333333;text-align:justify;font-family:arial;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:16px;color:#333333;text-align:justify;font-family:arial;background-color:#FFFFFF;\">\r\n	<span class=\"bjh-p\"><span class=\"bjh-br\"></span></span>\r\n</p>\r\n<p style=\"font-size:16px;color:#333333;text-align:justify;font-family:arial;background-color:#FFFFFF;\">\r\n	<span class=\"bjh-p\">2018年12月29日13时32分，微博账号“崔永元”发博文并附四张图片。经核实，其中两张图片所载内容与目前保存在最高人民法院档案处的(2011)民一终字第81号案件副卷的有关内容相同(其他两张为媒体报道截图)。</span>\r\n</p>\r\n<p style=\"font-size:16px;color:#333333;text-align:justify;font-family:arial;background-color:#FFFFFF;\">\r\n	<span class=\"bjh-p\"><span class=\"bjh-br\"></span></span>\r\n</p>\r\n<p style=\"font-size:16px;color:#333333;text-align:justify;font-family:arial;background-color:#FFFFFF;\">\r\n	<span class=\"bjh-p\">我们已经启动调查程序，欢迎崔永元教授等知情人向我们提供情况。如发现我院工作人员违反审判纪律问题，将依纪依法严肃处理。</span>\r\n</p>\r\n<p style=\"font-size:16px;color:#333333;text-align:justify;font-family:arial;background-color:#FFFFFF;\">\r\n	<span class=\"bjh-p\"><span class=\"bjh-br\"></span></span>\r\n</p>\r\n<p style=\"font-size:16px;color:#333333;text-align:justify;font-family:arial;background-color:#FFFFFF;\">\r\n	<span class=\"bjh-p\">联系方式：最高人民法院违法违纪举报系统，电话010-67556131。</span>\r\n</p>\r\n<p style=\"font-size:16px;color:#333333;text-align:justify;font-family:arial;background-color:#FFFFFF;\">\r\n	<span class=\"bjh-p\"><span class=\"bjh-br\"></span></span>\r\n</p>\r\n<p style=\"font-size:16px;color:#333333;text-align:justify;font-family:arial;background-color:#FFFFFF;\">\r\n	<span class=\"bjh-p\">中共最高人民法院机关纪委</span>\r\n</p>\r\n<p style=\"font-size:16px;color:#333333;text-align:justify;font-family:arial;background-color:#FFFFFF;\">\r\n	<span class=\"bjh-p\"><span class=\"bjh-br\"></span></span>\r\n</p>\r\n<p style=\"font-size:16px;color:#333333;text-align:justify;font-family:arial;background-color:#FFFFFF;\">\r\n	<span class=\"bjh-p\">2018年12月29日</span>\r\n</p>','2018-12-31',76,NULL,0),(29,1,'宋朝的历史旋涡，昭示改朝换代的秘密','山外青山楼外楼，西湖歌舞几时休？\r\n暖风熏得游人醉，直把杭州作汴州。','文化','<div style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#173943;\">\r\n	杭州在南宋时称作临安，听听这名，临时安置一下，没成想一下就安了一百多年。宋高宗赵构大概是不想管他爹和哥哥的死活的，反正又不是真的一家子。再说了，虎狼环伺的南宋，能苟安一隅已经很不错了。两宋加在一起三百余年，当时的GDP居全世界第一。可惜总为后人所诟病。\r\n</div>\r\n<div style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#173943;\">\r\n	<br />\r\n</div>\r\n<div style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#173943;\">\r\n	在很多人眼里，宋朝远不如秦，汉，唐这样的历史时期那么让人自豪和艳羡。其主要原因可能是因为宋朝太弱了，总被外族欺负，并且最终也被外族所灭。如果这样说的话，那么明朝同样也为外族所灭，为何有许多人还那么眷恋大明呢？\r\n</div>\r\n<div style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#173943;\">\r\n	<br />\r\n</div>\r\n<div style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#173943;\">\r\n	其实，明朝才真正是中国历史上最黑暗的时代。而满清更是将闭关锁国皇权独揽推行到极致，进一步导致了我中华整整落后了世界数百年。依我说，崖山之后，就再无华夏之国了，而《清明上河图》中的盛世镜像，也许现在只有河南开封的人们多少还可以津津乐道了。\r\n</div>\r\n<div style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#173943;\">\r\n	<br />\r\n</div>\r\n<div style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#173943;\">\r\n	公元960年，后周诸将发动陈桥兵变，赵匡胤黄袍加身建立宋朝。宋朝上承五代十国下启元朝，分北宋和南宋两个阶段，共历十八帝，享国319年。赵匡胤为避免晚唐藩镇割据和宦官专权乱象，采取重文抑武的施政方针，一方面加强了中央集权，另一方面剥夺武将兵权。宋太宗继位后统一全国，至宋真宗时期与辽国缔结澶渊之盟后逐渐步入治世。\r\n</div>\r\n<div style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#173943;\">\r\n	<br />\r\n</div>\r\n<div style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#173943;\">\r\n	1125年，金国大举南侵，徽钦二帝被虏宋室被迫南迁。宋高宗赵构在南京应天府继位建立南宋，绍兴和议后与金国以秦岭淮河为界建都临安，1276年元朝攻占杭州，崖山海战后，南宋灭亡。\r\n</div>\r\n<div style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#173943;\">\r\n	<br />\r\n</div>\r\n<div style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#173943;\">\r\n	宋朝是中国古代历史上商品经济、文化教育、科学创新高度繁荣的时代，北宋咸平三年（1000年）中国GDP总量为265.5亿美元，占世界经济总量的22.7%，人均GDP为450美元，超过当时西欧的400美元。后世虽认为宋朝“积贫积弱” ，但宋朝民间的富庶与社会经济的繁荣实远超盛唐。\r\n</div>\r\n<div style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#173943;\">\r\n	<br />\r\n</div>\r\n<div style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#173943;\">\r\n	尤其是北宋的都城汴梁，今开封，那时已经是人口达到150万的大城市，相比之下同时期的欧洲最大的城市不过是仅10万人口的威尼斯。宋朝时出现了宋明理学，儒学得到复兴，科技发展迅速，政治开明，且没有严重的宦官专权和军阀割据，兵变、民乱次数与规模在中国历史上也相对较少。北宋因推广占城稻人口迅速增长，从太平兴国五年（980年）的3710万人口，增至宣和六年（1124年）的1.2亿人。\r\n</div>\r\n<div style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#173943;\">\r\n	<br />\r\n</div>\r\n<div style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#173943;\">\r\n	史学家陈寅恪说：华夏民族之文化，历数千载之演进，造极于赵宋之世。而西方与日本史学界中认为宋朝是中国历史上的“文艺复兴”与经济革命时代的学者亦不在少数。宋朝的两度倾覆皆缘于外患，是唯独没有亡于内乱的王朝。\r\n</div>\r\n<div style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#173943;\">\r\n	<br />\r\n</div>\r\n<div style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#173943;\">\r\n	我觉得其实这一点，恰是宋朝最值得称道的，即使是到了亡国灭种的关头，仍旧有文天祥和陆秀夫这样的忠臣良将，宁可杀身成仁也不屈膝称臣。所以你眼中的“弱宋”其实真的不弱，只是在特殊的历史环境下和时期里，宋朝没能继续发展下去，不然，可能改写的不仅是中国历史，而是世界史。\r\n</div>\r\n<div style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#173943;\">\r\n	<br />\r\n</div>\r\n<div style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#173943;\">\r\n	我想这中间最重要的原因，一是蒙古的兴起和之后的横扫欧亚，二是高宗赵构的南迁。蒙古的兴起，应当讲不仅改变了中国的格局，也影响了世界。对于中国最大的影响则是摧毁了中原的汉文化，迫使中国的政治经济由北向南发展，也从某种意义上使中国的文化也更加走向单一和闭塞。从而阻碍了中国的自发资本主义的萌芽和扼杀了中国后来再次与世界接轨的机会，直至改革开放。\r\n</div>\r\n<div style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#173943;\">\r\n	<br />\r\n</div>\r\n<div style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#173943;\">\r\n	到明朝的虽再次一统但已经完美的错过了中国的关键发展期，虽然朱棣迁都北京，政治中心又回到了北方，但是我们的经济却慢慢的走向僵化和落后。到了清朝的康雍乾时期，我们将皇权政治和自甘堕落更加推向了极致，自嘉庆后在不到五十年间，中国迅速土崩瓦解开始了长达百年的耻辱时代。\r\n</div>\r\n<div style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#173943;\">\r\n	<br />\r\n</div>\r\n<div style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#173943;\">\r\n	而赵构南迁则是国人世界观和价值观的集中体现和历史映照，不怕死就怕不安逸。尽管有宋一朝，我们的国土面积一再缩水，直至偏安一隅，但是我们仍能安居乐业，保家卫国。并且还涌现了以岳飞为代表的“中兴四将”，可是我们最后总是埋怨是秦桧枉杀了岳飞，不然早就血洗耻辱还都开封了。是吗？不是的，是因为岳飞不懂政治，也不懂大势，秦桧只是忠实执行了高宗的政治意图。高宗一开始并不想当这个皇帝，但是一旦当上了怎能轻易撒手权力？\r\n</div>\r\n<div style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#173943;\">\r\n	<br />\r\n</div>\r\n<div style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#173943;\">\r\n	所以，赵构要的仍是一个在唯我独尊下的安定繁荣的国家，和一条可持续发展的道路。问题是已经快日薄西山的皇权政治体制我们虽依旧苟延残喘了数百年，但历史的发展已不再不相助宋朝，也不再眷顾我华夏。确切的讲，是世界历史的洪流从那时起把中国甩在一边了。\r\n</div>\r\n<div style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#173943;\">\r\n	<br />\r\n</div>\r\n<div style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#173943;\">\r\n	不过，若能穿越时空的话，我倒是愿意回宋朝。因为，那是一个大家辈出，文化、艺术最为繁荣鼎盛的时代，而且，老赵家优待文人，也是最为亲民的王朝。宋朝是奉行与士大夫共治天下的，所以重视教育且免费，在最高学府太学，居然还有餐补。还有居养法，也就是国家养老院，专门收容孤寡老人，孤儿弃婴。\r\n</div>\r\n<div style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#173943;\">\r\n	<br />\r\n</div>\r\n<div style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#173943;\">\r\n	两宋的制造业空前发达，工商业税约占70%，宋瓷远销海内外。老百姓也很有钱，平民穿丝绸、坐轿子根本不稀罕。最牛逼的当然是餐饮业和娱乐业，《清明上河图》里还有送外卖的，你可以仔细去找找。试想，辛弃疾能一天到晚没事就挑灯看剑，也至少是不愁吃喝，才能写出那么霸气的文章呀！\r\n</div>\r\n<div style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#173943;\">\r\n	<br />\r\n</div>\r\n<div style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#173943;\">\r\n	但是，最后大宋还是悲剧了，为什么？因为养兵的钱都白花了！别说宋军轻武就没有战斗力，主要是宋徽宗战略决策失误啊！联金灭辽，最后顺带把自己也灭了。\r\n</div>\r\n<div style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#173943;\">\r\n	<br />\r\n</div>\r\n<div style=\"color:#323E32;font-family:&quot;font-size:14px;background-color:#173943;\">\r\n	自南宋以后，世界的中心开始西移了。在此后的五百年间，西方经过地理大发现，文艺复兴和工业革命的洗礼与快速发展很快的超过了我们数千年积累下来的文明和财富。回望历史，也许我们真的感受颇深，可是真的只有痛定思痛、做出改变、付诸行动，才能真正实现国富民强。而不是要么醉生梦死的还是关起门来过小日子，要么叫嚣着什么梦什么梦实际是一片泡沫繁荣。因为历史发展的旋涡往往身处当中的我们在当时是看不清的，但是等到时过境迁，也许就不仅仅是改朝换代了。\r\n</div>','2019-01-02',6,NULL,0),(35,1,'刘梦溪：和钱锺书先生有通信但从未晤面妙趣无人可及 ','通信起因于1988年十一月中旬我和内子出差无锡，趁便访问了无锡新街巷钱先生的故宅。回京从一位小友处得知，钱先生已经获悉有人去他老宅拍照。这样一来，我们就不好秘而不宣了。于是我在当年的十二月一日，给钱先生写了一信，具道首尾缘由，并附呈在钱宅拍的照片四张，请求宽谅我们的冒昧。','娱乐','<p style=\"color:#2B2B2B;font-family:&quot;font-size:14px;background-color:#E0912F;text-indent:28px;text-align:justify;\">\r\n	<span style=\"line-height:21px;font-family:楷体, 楷体_GB2312, simKai;\"><span>梦溪同志：</span></span>\r\n</p>\r\n<p style=\"color:#2B2B2B;font-family:&quot;font-size:14px;background-color:#E0912F;text-indent:28px;text-align:justify;\">\r\n	<span style=\"line-height:21px;font-family:楷体, 楷体_GB2312, simKai;\"><span>来信使我惊愧交集。我是老病之人，知道你大病初愈，很有“相怜岂必病相同”（拙句）的情味。承寄照片，看了全不认得，也许房子变得太厉害了，更可能是我自己变得非复故我了。我对旧事不感兴趣，也懒去追忆，因为记忆是最善于捣鬼撒谎的，而忘怀不失为一种心理保健。来信说对拙著反复阅览，我就请你翻看《管锥编》497～8页论“华子病忘”的一节。我于1935年出国后，只回家两次，一次半天，一次一天一晚。吴女士不识何人。静汝侄女六年前曾来京相访。我不值得你费心研究，真诚地劝你放宽视野，抬高视线，另选目标。我自己有个偏见，考订作者的传记往往是躲避研究作品本身的防空洞。吴忠匡君的文章记事尚不失实，抄出的诗多不是我称心之作——也许当时没有给他抄存，也许他的诗识和我的不同。是否值得英译，我不便表示意见。中外一些刊物和学会邀请我挂名“顾问”、“理事”甚至“名誉主席”之类，我一概敬谢（得罪了不少同志）。虽然“政策是区别对待”，但是“法律面前人人平等”，未便破例，务请原谅。祖芬女士的文章，我们钦佩已久。你有这样一位“文章知己，患难亲人”，可喜可贺。杨绛的小书承你青眼，她也感愧得很。草此后谢，即叩</span></span>\r\n</p>\r\n<p style=\"color:#2B2B2B;font-family:&quot;font-size:14px;background-color:#E0912F;text-indent:28px;text-align:justify;\">\r\n	<span style=\"line-height:21px;font-family:楷体, 楷体_GB2312, simKai;\"><span>双安。</span></span>\r\n</p>\r\n<p style=\"color:#2B2B2B;font-family:&quot;font-size:14px;background-color:#E0912F;text-indent:28px;text-align:right;\">\r\n	<span style=\"line-height:21px;font-family:楷体, 楷体_GB2312, simKai;\"><span>钱锺书敬上，杨绛同候，十二月七日。</span></span>\r\n</p>\r\n<p style=\"color:#2B2B2B;font-family:&quot;font-size:14px;background-color:#E0912F;text-indent:28px;text-align:justify;\">\r\n	<span>凡我信中涉及的事项，钱先生一一作答，无一遗漏。因为我当时正在作的题目是《中国三大批评家：王国维、陈寅恪、钱锺书》，后来此书没有写成，原因倒不全是钱先生的忠告，而是读完钱之后读陈，竟陷进去未能出来，变成专门研究义宁之学了。由于我信中提及，有撰一钱、杨简谱之想，故钱先生回以“考订作者的传记往往是躲避研究作品本身的防空洞”的妙语。</span>\r\n</p>\r\n<p style=\"color:#2B2B2B;font-family:&quot;font-size:14px;background-color:#E0912F;text-indent:28px;text-align:justify;\">\r\n	<span>吴忠匡是钱先生尊人钱基博的学生，抗战时期曾一起赴湖南蓝田师范，以此知钱先生事甚详，所为文后来在《中国文化》创刊号上刊载。《中国文化》创刊时，原拟同时出版英文版，所以涉及钱诗的英译问题。我信中还婉转陈词，询问可否请他破例俯允《中国文化》之学术顾问，他的回答让人忍俊不禁：“虽然‘政策是区别对待’，但是‘法律面前人人平等’，未便破例，务请原谅。”而来示结尾处的“文章知己，患难亲人”一语，愚夫妇惊喜感愧，至今难以忘怀。</span>\r\n</p>\r\n<p style=\"color:#2B2B2B;font-family:&quot;font-size:14px;background-color:#E0912F;text-indent:28px;text-align:justify;\">\r\n	<span>\r\n	<p style=\"color:#2B2B2B;font-family:&quot;font-size:14px;background-color:#E0912F;text-indent:28px;text-align:justify;\">\r\n		<span>&nbsp;&nbsp;&nbsp;&nbsp;钱先生此信我们收到没有几日，又收到他另寄的一页法书，用的是荣宝斋宣纸印笺，上写《还家》诗一首：</span>\r\n	</p>\r\n	<p style=\"color:#2B2B2B;font-family:&quot;font-size:14px;background-color:#E0912F;text-indent:28px;text-align:center;\">\r\n		<span style=\"line-height:21px;font-family:楷体, 楷体_GB2312, simKai;\"><span>出郭青山解送迎，劫余弥怯近乡情。</span></span>\r\n	</p>\r\n	<p style=\"color:#2B2B2B;font-family:&quot;font-size:14px;background-color:#E0912F;text-indent:28px;text-align:center;\">\r\n		<span style=\"line-height:21px;font-family:楷体, 楷体_GB2312, simKai;\"><span>故人不见多新冢，长物原无只短檠。</span></span>\r\n	</p>\r\n	<p style=\"color:#2B2B2B;font-family:&quot;font-size:14px;background-color:#E0912F;text-indent:28px;text-align:center;\">\r\n		<span style=\"line-height:21px;font-family:楷体, 楷体_GB2312, simKai;\"><span>重觅钓游嗟世换，惯经离乱觉家轻。</span></span>\r\n	</p>\r\n	<p style=\"color:#2B2B2B;font-family:&quot;font-size:14px;background-color:#E0912F;text-indent:28px;text-align:center;\">\r\n		<span style=\"line-height:21px;font-family:楷体, 楷体_GB2312, simKai;\"><span>十年湖海浮沉久，又卧荒斋听柝声。</span></span>\r\n	</p>\r\n	<p style=\"color:#2B2B2B;font-family:&quot;font-size:14px;background-color:#E0912F;text-indent:28px;text-align:center;\">\r\n		<span style=\"line-height:21px;font-family:楷体, 楷体_GB2312, simKai;\"><span>（原注：寇乱前报更旧俗未改）</span></span>\r\n	</p>\r\n	<p style=\"color:#2B2B2B;font-family:&quot;font-size:14px;background-color:#E0912F;text-indent:28px;text-align:justify;\">\r\n		<span>诗后题识为：“余一九四六年回乡一宿故家，尔后未至无锡。梦溪祖芬贤伉俪寄示旧宅照片，因忆此诗，录呈雅教。”最后是钱先生的钤章。读钱先生的信，是一种享受，每一封都要抖几个包袱，至少也要抖一个。真的是妙趣横生，无人可及。1998年钱先生病逝时，我正在哈佛访学，内子的《不敢见钱锺书先生》一文，即写于北美，其中记叙我们与钱先生书信往还的一些细事，不妨参看。</span>\r\n	</p>\r\n	<p style=\"color:#2B2B2B;font-family:&quot;font-size:14px;background-color:#E0912F;text-indent:28px;text-align:justify;\">\r\n		<span>&nbsp;<span style=\"color:#2B2B2B;font-family:&quot;font-size:18px;background-color:#E0912F;\">虽然我未曾与钱先生一面，但他对我精神世界的影响可不小。八十年代中期至九十年代初，也许是以钱著为日课的缘故，梦里经常出现钱锺书先生，有时还有杨绛先生。真而且真，如同亲历。说起来，我的梦还真有点讲究。七十年代，经常梦见的人是周恩来总理。八十年代是钱先生。九十年代，时而会梦见余英时先生。21世纪头十年，梦中人经常是季羡林先生。梦到季先生或余英时先生，第二天我会打一个电话给他们。但梦见钱、杨两位先生，始终没敢惊动他们。</span><br />\r\n</span>\r\n	</p>\r\n<br />\r\n</span>\r\n</p>','2019-01-02',4,NULL,0),(36,1,'冬至古诗里的百味人生','在古人看来，冬至其实意味着冬天快要结束。明代文学家王鏊说：“冬至之日，一阳自地而升。”也就说，到来冬至这一天，土地里的阳气正悄然生成。','文化','<p style=\"color:#494949;font-family:&quot;font-size:14px;background-color:#F6F6F6;\">\r\n	<span>&nbsp;从这个意义来说，冬至的确是个重要的日子。古人会在这一天拜天祭祖；人们还会拜贺长辈，“冬至阳气起，君道长，故贺”（《汉书》）；冬至也会放假。《汉书·薛宣传》记载，一个叫张扶的官吏，冬至节照常办公，上司薛宣就教育他“吏以令休，所有来久。曹虽有公职事，家亦望私恩意”，要他赶快回家，准备酒食，宴请邻里亲朋。由此可见，在古代，如果这一天不和家人团聚，有点不合人情。</span>\r\n</p>\r\n<p style=\"color:#494949;font-family:&quot;font-size:14px;background-color:#F6F6F6;\">\r\n	<span>&nbsp;&nbsp;&nbsp;&nbsp;白居易有一首《邯郸冬至夜思家》这样写：“邯郸驿里逢冬至，抱膝灯前影伴身。想得家中夜深坐，还应说着远行人。”大意是说，冬至节本该和家里人在一起，但诗人却出差到了邯郸，孤孤单单，想象家中亲人正在谈论自己这个远行人。</span>\r\n</p>\r\n<p style=\"color:#494949;font-family:&quot;font-size:14px;background-color:#F6F6F6;\">\r\n	<span>&nbsp;&nbsp;&nbsp;&nbsp;更重要的是，适合与家人团聚的冬至，也能激发起人们的无限情思：对深爱之人的怀念。在很多诗词里，人们能读到这种深情。白居易写过一首《冬至夜怀湘灵》，这和他刻骨铭心的初恋有关。白居易11岁时，随母来到父亲白季庚任官所在地——徐州符离（今安徽省宿县境内），在那里，他认识了一个叫湘灵的邻家女孩。姑娘长得漂亮，很会唱歌，俩人青梅竹马，是朝夕不离的玩伴。19岁时，白居易写了一首诗，名为《邻女》，诗里描绘了十五岁的湘灵，是如何的美丽以及嗓音是如何的悦耳。贞元十六年（公元800年）初，29岁的白居易考上进士，他向母亲请求与湘灵结婚，遭到母亲拒绝。过了四年，白居易做了校书郎，再次苦求母亲，母亲不但不同意，还不让他们不再见面。白居易全家也搬离徐州。《冬至夜怀湘灵》就写于这个时期：“艳质无由见，寒衾不可亲。何堪最长夜，俱作独眠人。”冬至团圆夜，有情人被棒打作两头飞，长夜漫漫，孤独入眠。这段恋爱悲剧是白居易一生的痛楚，他写了不少送给湘灵的诗歌。千古名篇《长恨歌》的原始创作动机，也是源自自身的情感悲剧。“天长地久有时尽，此恨绵绵无绝期”，诗人白居易借李隆基与杨贵妃之间的爱情悲剧，抒发他自身永久之“恨”。</span>\r\n</p>\r\n<p style=\"color:#494949;font-family:&quot;font-size:14px;background-color:#F6F6F6;\">\r\n	<span>&nbsp;&nbsp;&nbsp;&nbsp;无独有偶，在七百多年后的朝鲜半岛，有一名真性情的女子，也在冬至之夜写下了对爱情的执着。这位女子就是黄真伊。影视迷们都知道，2006年，韩国影星河智苑主演了一部古装韩剧《黄真伊》，第二年宋慧乔也主演了同名电影，电视剧和电影都讲述了一个名叫黄真伊的奇女子的不平凡一生。黄真伊（约1506年至1544年）在历史上确有其人，生活在朝鲜李朝中宗时期的开城（旧称“松都”），她与朴渊瀑布（著名景色）、硕儒徐敬德并称“松都三绝”——瀑布是“绝胜”，徐敬德是“绝伦”，而她是“绝色”。</span>\r\n</p>\r\n<p style=\"color:#494949;font-family:&quot;font-size:14px;background-color:#F6F6F6;\">\r\n	<span>&nbsp;&nbsp;&nbsp;&nbsp;黄真伊是韩国著名艺伎，风华绝代，部分诗作还入选了韩国中学课本。她的《冬至夜》写得很有情思：“截取冬至夜半强，春风被里屈幡仓，有灯无月郎来夕，曲曲铺舒寸寸长”。把漫长的冬至夜剪下，卷起来放在温香如春风的被子里，情郎来的夜晚，再一寸寸将它摊开。朝鲜文学家柳梦寅评论黄真伊“女中之倜傥任侠人”（《于于野谈》），从这大胆新奇的小诗可以看出，此言不虚。李朝中宗时期，相当于我国明朝正德嘉靖年间，社会思潮亦是程朱理学，但就在对女性管束严苛的时代，出现了睥睨一切的黄真伊。她让面壁三十载、声望极高的知足禅师破戒；她还勾引过徐敬德，但徐坐怀不乱，黄真伊甚为感佩，便拜其为师，亦成为一段佳话。</span>\r\n</p>\r\n<p style=\"color:#494949;font-family:&quot;font-size:14px;background-color:#F6F6F6;\">\r\n	<span>&nbsp;&nbsp;&nbsp;&nbsp;当然，在冬至的诗歌里不都是惆怅伤感。王安石的《冬至》，描述了冬至节这天的热闹欢庆：“都城开博路，佳节一阳生。喜见儿童色，欢传市井声。幽闲亦聚集，珍丽各携擎。却忆他年事，关商闭不行。”这是少有的一抹亮色，为何？为了富国强兵，在宋神宗的支持下，王安石进行了有名的“熙宁变法”，世称“王安石变法”。这场变法在新旧党争之中，时断时续，勉强维持了15年，有弊端，也有效果，社会经济得到发展，呈现出繁荣景象。所以，王安石在诗里不无自诩之意。</span>\r\n</p>\r\n<p style=\"color:#494949;font-family:&quot;font-size:14px;background-color:#F6F6F6;\">\r\n	<span>&nbsp;&nbsp;&nbsp;&nbsp;可惜的是，王安石的变法，当时就遭到很多人的反对。宋神宗死后，王安石失去了最有力的支持力量，变法随即废止，王安石亦抑郁而亡。到了南宋，为了给父兄宋徽宗、宋钦宗开脱“靖康之耻”的历史罪责，宋高宗诏命重修《神宗实录》，把“国事失图”由蔡京上溯至王安石，认为是王安石乱祖宗法度，祸国殃民，才招致了可怕的后果。可是，即便否定王安石的功劳，依然救不回徽宗和钦宗，也改变不了南宋半壁江山常受金兵侵扰的现实。此后，扬州多次遭受破坏。1176年，22岁的姜夔路过扬州，又碰到大雪，扬州破败萧条，他写下了震古烁今的《扬州慢》：“过春风十里。尽荠麦青青。自胡马窥江去后，废池乔木，犹厌言兵。”《扬州慢》有一段小序：“淳熙丙申至日，予过维扬。夜雪初霁，荠麦弥望。”姜夔详细记载了时间、地点和天气，“至日”就是冬至日。（毛春晖） &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span>\r\n</p>','2019-01-02',5,NULL,0),(37,1,'年轻人的社交降级来得太猛烈 ','自从微信、微博等社交软件占据了睡觉以外的其他生活场域，人与人之间的关系就在以你察觉不到的速度发生着重重异化。','娱乐','<p style=\"color:#B0B6B0;font-family:&quot;font-size:14px;background-color:#62625A;\">\r\n	中枪的当然不只你一个。超过半数的都市青年都将大把的时光交给了电子屏幕，过去那些依托于实体的社会关系正在发生着不同程度的瓦解，连社交软件上的种种“展示”本身都在不知不觉间降了级......不信你看：\r\n</p>\r\n<p style=\"color:#B0B6B0;font-family:&quot;font-size:14px;background-color:#62625A;\">\r\n	—\r\n</p>\r\n<p style=\"color:#B0B6B0;font-family:&quot;font-size:14px;background-color:#62625A;\">\r\n	恋 爱 降 级\r\n</p>\r\n<p style=\"color:#B0B6B0;font-family:&quot;font-size:14px;background-color:#62625A;\">\r\n	<p style=\"color:#B0B6B0;font-family:&quot;font-size:14px;background-color:#62625A;\">\r\n		虽然年轻人们嘴上嚷嚷着“想脱单”，行动上却一度迟缓：在暧昧的边缘久久试探，却迟迟不肯做出最后的承诺，“不主动、不拒绝、不负责”已成为恋爱降级的一大征兆。\r\n	</p>\r\n	<p style=\"color:#B0B6B0;font-family:&quot;font-size:14px;background-color:#62625A;\">\r\n		而对于处在恋爱关系之中的男女们，一种新型恋爱模式也在不知不觉间风靡起来：约会开销一切AA，消息爱回不回，即使base同一座城市，也悄咪咪谈起了“同城异地恋”......\r\n	</p>\r\n	<p style=\"color:#B0B6B0;font-family:&quot;font-size:14px;background-color:#62625A;\">\r\n		—\r\n	</p>\r\n	<p style=\"color:#B0B6B0;font-family:&quot;font-size:14px;background-color:#62625A;\">\r\n		暧 昧 降 级\r\n	</p>\r\n	<p style=\"color:#B0B6B0;font-family:&quot;font-size:14px;background-color:#62625A;\">\r\n		<p style=\"color:#B0B6B0;font-family:&quot;font-size:14px;background-color:#62625A;\">\r\n			时不时送礼、定期约出来交流感情的“追心上人法则”已经过时，当代年轻人连养备胎都发展出了一套线上模式：朋友圈时不时点个赞（在对方发照片时尤佳），评论区由衷地（套路地）发送几句赞美（备胎之间相互认识的请慎用），私聊里定期嘘寒问暖一番......就能让有的人内心小鹿乱撞，暧昧成本可谓逐年下滑。\r\n		</p>\r\n		<p style=\"color:#B0B6B0;font-family:&quot;font-size:14px;background-color:#62625A;\">\r\n			—\r\n		</p>\r\n		<p style=\"color:#B0B6B0;font-family:&quot;font-size:14px;background-color:#62625A;\">\r\n			<p style=\"color:#B0B6B0;font-family:&quot;font-size:14px;background-color:#62625A;\">\r\n				同 事 关 系 降 级\r\n			</p>\r\n		</p>\r\n		<p style=\"color:#B0B6B0;font-family:&quot;font-size:14px;background-color:#62625A;\">\r\n			对于深谙线上办公软件的上班族而言：同一部门互相认识的同事，在洗手间/电梯间等场所偶遇时，冷场几率高达90%；比起同事的真实姓名，见面时更易脱口而出的可能是对方的微信昵称；即使你们的工位直线距离不超过10米，能在线上说完的事也绝不会拉到线下......毕竟，活在软件里，才是你们之间最美妙的距离。\r\n		</p>\r\n		<p style=\"color:#B0B6B0;font-family:&quot;font-size:14px;background-color:#62625A;\">\r\n			—\r\n		</p>\r\n		<p style=\"color:#B0B6B0;font-family:&quot;font-size:14px;background-color:#62625A;\">\r\n			论 据 降 级\r\n		</p>\r\n		<p style=\"color:#B0B6B0;font-family:&quot;font-size:14px;background-color:#62625A;\">\r\n			<p style=\"color:#B0B6B0;font-family:&quot;font-size:14px;background-color:#62625A;\">\r\n				过去，无论是写作文还是跟人口头争论，“引经据典”都是从小养成的习惯，那些甭管是不是鲁迅本尊说过的话，转眼就被套上了周先生的帽子......\r\n			</p>\r\n			<p style=\"color:#B0B6B0;font-family:&quot;font-size:14px;background-color:#62625A;\">\r\n				谁知有一天，你在不小心围观到的一场网络论战中发现：乱用名言什么的已经过时了，引用中小学语文课本中的诗词也算好的，终极大招是对方甩手就是一句：“某奇葩说辩手XXX说过@#%^&amp;......”徒留你在原地目瞪口呆。\r\n			</p>\r\n			<p style=\"color:#B0B6B0;font-family:&quot;font-size:14px;background-color:#62625A;\">\r\n				—\r\n			</p>\r\n			<p style=\"color:#B0B6B0;font-family:&quot;font-size:14px;background-color:#62625A;\">\r\n				信 仰 降 级\r\n			</p>\r\n			<p style=\"color:#B0B6B0;font-family:&quot;font-size:14px;background-color:#62625A;\">\r\n				与其虔诚地祈求看不见的上帝和菩萨，当代唯物主义青年们早已在现实世界中找到了自己的精神依托：定期转发自带好运光芒的王校长、超越妹妹和信小呆显然已经无法满足“锦鲤信徒们”了，新晋的各种人格、运势、星盘测试正在风靡朋友圈，随便滑动手机屏幕，你会恍若置身一个“大型算命现场”......\r\n			</p>\r\n			<p style=\"color:#B0B6B0;font-family:&quot;font-size:14px;background-color:#62625A;\">\r\n				—\r\n			</p>\r\n			<p style=\"color:#B0B6B0;font-family:&quot;font-size:14px;background-color:#62625A;\">\r\n				朋 友 圈 降 级\r\n			</p>\r\n<span style=\"color:#B0B6B0;font-family:&quot;font-size:14px;background-color:#62625A;\">即便是作为当代人最大型社交场所之一的微信朋友圈，也在不知不觉间“变了味”：一条社交朋友圈发出之后，给你点赞的人里有1/3没见过面；那些刚用微信时记录下的陈年旧事，伴随着不带滤镜美颜的照片一起，都被你锁进了茫茫的信息之海，嫌麻烦的人超前一步，早早设置了“三天可见”，从此过上加好友再无隐忧的舒适生活。</span>\r\n		</p>\r\n	</p>\r\n	<div>\r\n		<br />\r\n	</div>\r\n</p>','2019-01-02',10,NULL,0),(38,1,'智能手表如何救人一命？','Ryan Clark 曾经是一名游戏开发者，他为 Pebble 智能手表打造了一款能够救癫痫患者一命的 App。这款名为「Pebble 癫痫监测」的应用能够在用户癫痫发作的时候及时察觉，并且通知到指定的联系人。','娱乐','<p style=\"background-color:#E0E0E0;font-size:20px;font-family:Lato, &quot;\">\r\n	<span style=\"line-height:21px;font-size:14px;\">这款 App 能够发挥作用其原理是利用了 Pebble 智能手表内置的加速度计，这样就可以及时判断出用户的行动模式是否符合癫痫发作的特征，以便使用短信通知到家人朋友来救命。据估计，在美国每年有 50000 人死于癫痫发作，或因为癫痫导致的结果而殒命。</span>\r\n</p>\r\n<p style=\"background-color:#E0E0E0;font-size:20px;font-family:Lato, &quot;\">\r\n	<span style=\"line-height:21px;font-size:14px;\">虽然别的可穿戴设备比如像 Apple Watch 也内置了加速度计，但是 Pebble 为 App 开发者提供了独特的机会。有一些好的理由可以用于解释为何这款癫痫探测 App 最适合在 pebble 手表上使用。</span>\r\n</p>\r\n<p style=\"background-color:#E0E0E0;font-size:20px;font-family:Lato, &quot;\">\r\n	<span style=\"line-height:21px;font-size:14px;\">「我没有考虑在其他的智能手表比方说 Apple watch 上开发这款应用是经过深思熟虑的，」Clark 解释道，「首先就是价格问题，患病的人通常都有着较大的经济压力，Apple Watch 可比 Pebble 贵多了。」</span>\r\n</p>\r\n<p style=\"background-color:#E0E0E0;font-size:20px;font-family:Lato, &quot;\">\r\n	<span style=\"line-height:21px;font-size:14px;\">这话说的一点没错，Pebble 起价仅为 99 美元，而 Apple Watch 起价就要 349 美元。</span>\r\n</p>\r\n<p style=\"background-color:#E0E0E0;font-size:20px;font-family:Lato, &quot;\">\r\n	<span style=\"line-height:21px;font-size:14px;\">还有一点重要的原因就是像 Apple Watch 或者是 Moto 360 这样的智能手表不允许你下载使用 开源 App，而 Pebble 不仅为开发者提供了简便易用的开发工具，还允许设备使用者自行下载并安装开源软件。换句话说，你不需要像使用 Apple Watch 那样仅能依靠经由 App Store 核准的软件。</span>\r\n</p>\r\n<p style=\"background-color:#E0E0E0;font-size:20px;font-family:Lato, &quot;\">\r\n	<span style=\"line-height:21px;font-size:14px;\">癫痫基金会的首席运营官 Angela Ostrom 表示这种监控设备能够为癫痫患者提供巨大的帮助。「虽然现在还没有任何一种设备可以阻止癫痫发作，也不是所有的癫痫患者都有机会使用到这款应用，但是这种检测癫痫发作活动的智能设备将会使得许多家庭和患者从中收益，在患者可能正遭遇危险的时候及时发出警报。」</span>\r\n</p>\r\n<p style=\"background-color:#E0E0E0;font-size:20px;font-family:Lato, &quot;\">\r\n	<span style=\"line-height:21px;font-size:14px;\">这款 App 不仅可以通知到癫痫患者的亲朋好友，还可以帮助他们定位发病者的位置，确认发病者的呼吸状况，并且在需要的时候寻求医疗救助。</span>\r\n</p>\r\n<p style=\"background-color:#E0E0E0;font-size:20px;font-family:Lato, &quot;\">\r\n	<span style=\"line-height:21px;font-size:14px;\">\r\n	<p style=\"background-color:#E0E0E0;font-size:20px;font-family:Lato, &quot;\">\r\n		<span style=\"line-height:21px;font-size:14px;\">「Pebble 癫痫监测」并非完美，它还面临着监管与医疗责任的问题。这款应用还没有通过 FDA（美国食品药品监督管理局）的批准，因此还不能被认证为一款「医疗设备」。同时由于它并非是经由应用商店下载，用户实际上需要从好几个地方下载并且将它们拼凑在一起，因此使用中的责任就需要用户自己来承担了。</span>\r\n	</p>\r\n	<p style=\"background-color:#E0E0E0;font-size:20px;font-family:Lato, &quot;\">\r\n		<span style=\"line-height:21px;font-size:14px;\">同时这款应用还面临着一些硬件上面的局限。「Pebble 癫痫监测」是否能发挥作用并没有经过科学的认证，如果佩戴手表的患者在癫痫发作期间手臂无法移动的话，它就不能发挥监测效果。而且「Pebble 癫痫监测」还有可能会发出错误的判断，不过经过设置可以暂时禁用该功能。虽然 Pebble 作为一款相对便宜且开放的智能手表在价格上很具有吸引力，但是它的硬件配置并非最强。</span>\r\n	</p>\r\n	<p style=\"background-color:#E0E0E0;font-size:20px;font-family:Lato, &quot;\">\r\n		<span style=\"line-height:21px;font-size:14px;\">Clark 本人也承认像 Apple Watch 那样配有心率传感器的智能手表会是更为理想的载体，如果它能够支持开源软件的话。</span><span style=\"line-height:21px;font-size:14px;\">「如果能够监控到癫痫发作时常常伴有的心率波动，其实癫痫监测 &amp; nbsp;App 还能够做的更好，也能避免虚假警报。」</span>\r\n	</p>\r\n	<p style=\"background-color:#E0E0E0;font-size:20px;font-family:Lato, &quot;\">\r\n		<span style=\"line-height:21px;font-size:14px;\">不过现在这款 App 对于 Clark 和他的家人来说已经很不错了，他的妻子 Kathryn 表示这款应用给了自己心灵的安慰。</span>\r\n	</p>\r\n	<p style=\"background-color:#E0E0E0;font-size:20px;font-family:Lato, &quot;\">\r\n		<span style=\"line-height:21px;font-size:14px;\">「如果你患有癫痫并且时常发作，就必须认真考虑对策，找出能够带病生存的方法。这款监测癫痫的应用对于我们来说也是一种应对方式。」</span>\r\n	</p>\r\n	<p style=\"background-color:#E0E0E0;font-size:20px;font-family:Lato, &quot;\">\r\n		<span style=\"line-height:21px;font-size:14px;\">Clark 不确定他的 App 能否引来更多人关注癫痫，但是他的确希望能有越来越多的人了解这种疾病。</span><span style=\"line-height:21px;font-size:14px;\">「大部分人对于癫痫所知甚少，他们往往也不知道在癫痫患者发病时如何施救。」Clark 建议人们通过 Purple Day 等相关公益活动加深对于癫痫的了解。</span>\r\n	</p>\r\n<br />\r\n</span>\r\n</p>','2019-01-02',16,NULL,0),(39,1,'开车给手机充电 后果居然这么严重！ ','开车打手机的事情，已经说过很多次了，那是极其危险的！今天，又得知一件开车与手机之间关系的事情，那就是：开车时给手机充电，居然也会给地球变暖“做贡献”！','娱乐','<p style=\"color:#323E32;font-size:14px;background-color:#007AB1;font-family:&quot;\">\r\n	&emsp;国外媒体报道，开车时给手机充电不但浪费资源，还会造成严重的环境污染。\r\n</p>\r\n<p style=\"color:#323E32;font-size:14px;background-color:#007AB1;font-family:&quot;\">\r\n	&emsp;&emsp;这是因为手机在充电时，每个USB端口会把<strong>每加仑汽油（约3.8升）的行驶里程降低0.03英里（约0.48公里）。</strong>在美国，司机每年行驶的里程约为3万亿英里，以每加仑平均30英里的速度计算，<strong>手机充电每年会额外使用1亿加仑的汽油，成本约为2亿美元。</strong>而这额外消耗的1亿加仑汽油，<strong>产生了约97万吨能让地球变暖的二氧化碳。</strong>\r\n</p>\r\n<p style=\"color:#323E32;font-size:14px;background-color:#007AB1;font-family:&quot;\">\r\n	&emsp;&emsp;另外，据一家环境保护机构称，按照美国每辆汽车为一部手机充电计算，每年产生的额外的二氧化氮数量相当于185257辆客运车辆所产生的二氧化碳，同时也相当于燃烧9.45亿磅（约合4.3亿公斤）煤炭所造成的污染。\r\n</p>\r\n<p style=\"color:#323E32;font-size:14px;background-color:#007AB1;font-family:&quot;\">\r\n	&emsp;&emsp;由于汽车厂商仍在继续为汽车增加可为手机充电的USB端口，相信这种情况还会进一步加剧。美国一家汽车零部件制造商的工程副总裁马力·古思坦斯基称：“当前汽车上的USB端口数量远未达到上限。而且，随着技术的创新，USB端口不仅能为手机充电，甚至还能连接无线。因为消费者想把汽车当成家。”\r\n</p>\r\n<p style=\"color:#323E32;font-size:14px;background-color:#007AB1;font-family:&quot;\">\r\n	&emsp;&emsp;这不仅仅是一个环境问题。随着各种消费电子产品的普及，仪表盘触摸屏及其他技术的发展，12V的汽车供电系统将逐渐被淘汰。一些汽车厂商已经表示，未来型号产品将配备48V供电系统。\r\n</p>\r\n<p style=\"color:#323E32;font-size:14px;background-color:#007AB1;font-family:&quot;\">\r\n	&emsp;&emsp;调研公司IHS数据显示，去年美国售出了1460万辆具有USB充电端口的汽车，而2005年约为330万辆。IHS预计，2022年该数字将增加到1670万辆。全球范围内，2022年将售出8500万辆具有USB充电端口的汽车，而去年约为4900万辆。\r\n</p>\r\n<p style=\"color:#323E32;font-size:14px;background-color:#007AB1;font-family:&quot;\">\r\n	&emsp;&emsp;IHS的报告并未提及每辆汽车拥有多少个USB端口。以克莱斯勒去年推出的小型货车Pacifica为例，该车拥有9个USB充电端口，高于任何一辆汽车。\r\n</p>\r\n<p style=\"color:#323E32;font-size:14px;background-color:#007AB1;font-family:&quot;\">\r\n	&emsp;&emsp;到目前为止，智能手机最经济的充电方式就是在家充电。按照每加仑汽油2美元计算，<strong>车内充电的成本是每小时2美分</strong>。而<strong>在家充电每小时成本为0.06美分，</strong>仅为车内充电的3%。\r\n</p>','2019-01-02',13,NULL,0),(40,1,'斩断象牙黑市的快递走私渠道 ','议论风生\r\n　　大量“潜规则”和已经查处的案例说明，大量的象牙及其制品正是通过快递环节的验收漏洞，完成了卖家与买家的对接。','娱乐','<p style=\"color:#494949;font-family:&quot;font-size:14px;background-color:#E7EEF4;\">\r\n	根据《国务院办公厅关于有序停止商业性加工销售象牙及制品活动的通知》的要求，我国自2018年1月1日起全面停止加工销售象牙及制品活动。但新京报记者调查发现，象牙禁令实行一年来，一些“圈内人”因制售暴利，仍在从事象牙制品的走私及贩卖。在象牙走私链条中，很多团伙通过边境将整牙走私入境，然后切片通过快递发往各地。\r\n</p>\r\n<p style=\"color:#494949;font-family:&quot;font-size:14px;background-color:#E7EEF4;\">\r\n	&emsp;&emsp;全面停止加工销售象牙及制品活动一年来，相关部门加大了对于象牙走私及售卖的稽查力度。但并不意味着象牙制品的走私和贩卖现象就此绝迹，不少走私行为只是相对更隐蔽了。\r\n</p>\r\n<p style=\"color:#494949;font-family:&quot;font-size:14px;background-color:#E7EEF4;\">\r\n	&emsp;&emsp;这主要体现在两个方面。一是，制作和交易更流向于“熟人圈”内进行；另一个是象牙走私的“线上交易”属性越来越强，很多买家和卖家从联系、看货到交易都在网上完成。事实上，早在2013年，就有媒体报道，不管是内陆还是沿海，在巨额利润的诱惑下，通过互联网进行的象牙走私活动暗流涌动。象牙走私线上化，除了互联网普及带来的交流成本的降低，另一个重要条件便是快递网络的无缝对接。\r\n</p>\r\n<p style=\"color:#494949;font-family:&quot;font-size:14px;background-color:#E7EEF4;\">\r\n	&emsp;&emsp;检索过往的相关案例可知，“快递”是不少象牙走私案件中的一个关键词。如2015年合肥中院审理的一个走私大案中，就有“男子为走私象牙，专门应聘当快递员”；2017年河南襄城县一起涉嫌非法收购、出售珍贵、濒危野生动物制品案件中，一快递员因协助非法运输野生动物制品被提起公诉；去年11月，深圳海关破获的“404”特大象牙走私案，犯罪嫌疑人也是利用“快递走私象牙”……种种细节表明，要切断象牙走私的流通渠道，强化快递环节的把关至关重要。\r\n</p>\r\n<p style=\"color:#494949;font-family:&quot;font-size:14px;background-color:#E7EEF4;\">\r\n	&emsp;&emsp;2016年底发布的《禁止寄递物品管理规定》，明确将象牙、虎骨、犀牛角等濒危野生动物及其制品列入禁寄范畴。但在现实中，禁寄规则执行有不小的模糊操作空间。此次报道中就有“圈内人”透露，“货千万别发空运，走陆运，别得罪快递员就行。”并且为了安全，一般会固定委托一个快递员来负责发货，“每次发货，红包跟上，就没事。”甚至，有快递员曾经面临警方抽查时，为“收货人”电话报信助其逃避检查。\r\n</p>\r\n<p style=\"color:#494949;font-family:&quot;font-size:14px;background-color:#E7EEF4;\">\r\n	&emsp;&emsp;上述“潜规则”和已经查处的案例说明，大量的象牙及其制品正是通过快递环节的验收漏洞，完成了卖家与买家的对接。而只要“别得罪快递员就行”，很可能说明禁寄规则有鸡肋化之嫌。如果快递行业的禁寄物品的把关、审核，只有快递员一个环节，其中的擦边球和可操作的空间就可想而知。当下一般用户寄送普通药品都要求提供购买发票，但是，一遇到“特殊”客户是否就能够开“后门”，值得追问。如果禁寄规则只对普通人有效，而无法真正阻挡那些最应该被禁寄的物品，显然违背了初衷。\r\n</p>\r\n<p style=\"color:#494949;font-family:&quot;font-size:14px;background-color:#E7EEF4;\">\r\n	&emsp;&emsp;理论上，象牙及其制品等禁寄且违法的物品通过快递寄送，可以触发联动的报警机制。相关部门在这个环节不妨加大对接力度，有效堵住快递走私的漏洞。\r\n</p>\r\n<p style=\"color:#494949;font-family:&quot;font-size:14px;background-color:#E7EEF4;\">\r\n	&emsp;&emsp;强调快递验收环节的把关责任，并不是说要把打击象牙走私的重心转移到快递行业。而是说，象牙走私作为一个非法产业链，只有每一个环节都堵住了，才能最大限度压缩其活动空间。流通渠道被限制，非法走私的“出货”难度和成本提高，其“生意”空间自会越来越小。\r\n</p>','2019-01-02',3,NULL,0),(41,1,'年底这十大案件呈高发“捞一把”过年 ','   年底特别是春节前，人们购物、送礼、聚会、银行取款、商店备货等情形相对集中，同时犯罪分子也借此“捞财”疯狂作案，导致某些犯罪呈增多态势。犯罪分子乘年终之际，\"狠捞一把\"的思想，促使各类侵财类案件处于高发态势。','新闻','<p align=\"left\" style=\"color:#464646;font-family:Verdana, 宋体, sans-serif;background-color:#7798A3;\">\r\n	<span>&nbsp;几类主要多发性案件<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;1、入室盗窃居民住宅案件。乘着年终临近，居民外出旅游、走亲访友、聚会等活动的增多，致使许多居民家中白天或者夜间无人居住成为安全防范盲点，犯罪分子极有可能乘此机会再次疯狂作案。<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;2、盗窃车内财物案件。年终，市民在外用餐、游玩频率增加。同时，城市专业停车场数量以及容量与汽车流量的不平衡，使得大多数在外停放的汽车无专人看管，在加上长假期间，车内往往会放有一些贵重财物，极易成为犯罪分子的作案目标。<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;3、盗窃单位、商店案件。随着岁末的到来，机关、企事业单位将陆续放假，安全防范意识也相对放松下来，商店门市更是迎来经营高峰期，此时部分机关、厂企单位、商店门市的人防、物防、技防力量相对簿弱，这就给犯罪分子提供了可乘之机，利用单位长假期间厂区、办公区域无人之际伺机下手作案，盗取工业原材料及办公室内的现金、电脑等财物，同时犯罪分子也利用商店门市节假日期间现金流量较多、烟酒等价高物品进货量大、人流量较大，且白天或者夜间商店门市值守力量又相对薄弱的特点，采用撬卷帘门、撬窗、翻墙、乘隙等手段作案。<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;4、\"两抢\"案件。岁末年终，路上行人增多，且随身一般都会携带大量财物，尤其单身女子随身携带的项链、拎包、手机等财物则成为犯罪分子的重点作案目标，因此飞车抢夺案件会日渐反弹，同时极易引发拦路抢劫等恶性案件。<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;5、街头诈骗案件。犯罪分子利用外出人流增多、随身携带较多现金这一特点，在银行、超市、公园、汽车站、火车站、医院等门口采用迷信、拾物平分等手段，团伙或结伙作案骗取受害人钱财。<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;6、扒窃、拎包案件。年终假日期间，市民一般多会选择出外游玩或者购物，且往往都会携带有大量现金。同时，因为人流量的增多，购物街、商场饭前、旅游景点等地方人员相对密集，群众警惕性相对放松。犯罪分子往往容易利用这一机会，进行扒窃、拎包作案。以往长假的警情，也表明该类警情有明显上涨的趋势。<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;7、利用ATM取款机盗窃案件。购置年货、礼品频繁，由于各商业中心群众集中购物，加之银行网点放假。市民使用ATM取款机机率增加。犯罪分子会将商业场所附近的自动取款机作为作案地点。作案手段多为安装读卡装置窃取用户的银行卡信息资料、安装夹钞装置盗走装置内夹住的现金、安装吞卡装置盗走银行卡取现、\'掉包\'换卡取走卡内现金、直接取走用户遗忘在ATM自动取款机内的银行卡现金、破坏ATM自动取款机盗取机内现金。<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;8、盗窃电动车、自行车案件。年底，市民外出活动增多，加之部分地方停车场地紧张、看护措施薄弱，在商场、景点周边盗窃电动车、自行车案件存在多发风险。<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;9、伪造代金卡、代金券骗取财物案件。年终岁末，商家常印制并出售大量代金卡、代金券，由于这些代金卡、代金券不属于法律意义上的有价证券，在印制和使用上缺少相关规定和必备的防伪标志，使得一些不法分子有空可钻，常以伪造的代金卡、代金券诈骗商家货物。<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;10、偷窃家禽家畜。随着天气寒冷、年终聚餐频繁，家禽家畜价格飞涨，农户防范意识薄弱，盗窃农村家禽家畜案件有逐渐增多趋势，通常采用撬门、割绳、现场宰杀的手段盗窃羊、鸡、兔等家禽家畜，社会影响恶劣，易引发恶性案件。经营者要增强防范意识。</span>\r\n</p>\r\n<p align=\"left\" style=\"color:#464646;font-family:Verdana, 宋体, sans-serif;background-color:#7798A3;\">\r\n	&nbsp;\r\n</p>','2019-01-02',2,NULL,0),(43,1,'博客连载：无遗憾不青春，每个人都有过的热血青春，在这8本书里','“成年后的我们，总能演好别人，唯独忘了自己。”\r\n\r\n\r\n\r\n王小波在《黄金时代》里说：“那一年，我二十一岁，在我一生的黄金时代，我有好多奢望，我想爱，想吃，想在一瞬间变成天上半明半暗的云。”','娱乐','<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n	<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">后来我才知道，生命，就是一个把梦想变成现实的过程。</span></span>\r\n</p>\r\n<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n	<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\"><br />\r\n</span></span>\r\n</p>\r\n<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n	<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">过了青春时段，大部分人在生活中表情平静，心情克制。</span></span>\r\n</p>\r\n<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n	<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\"><br />\r\n</span></span>\r\n</p>\r\n<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n	<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">人们早忘了，那种为一点小事开怀大笑或痛哭流涕的的敏感、矫情和小时候天马行空对未来的幻想。</span></span>\r\n</p>\r\n<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n	<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\"><br />\r\n</span></span>\r\n</p>\r\n<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n	<span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\"><b><span><span>今天，为你推荐博客</span>APP上的这几本书，关于青春和理想。</span></b></span>\r\n</p>\r\n<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n	<span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\"><b><span><br />\r\n</span></b></span>\r\n</p>\r\n<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n	<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">希望能陪你一起回忆那段鲜衣怒马的年少时光。</span></span>\r\n</p>\r\n<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n	<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\"><br />\r\n</span></span>\r\n</p>\r\n<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n	<span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\"><u><b><span>PS：小伙伴们，博客APP最近新上线了</span><span>“博客书”</span><span>功能，排版精美，制作精细，内容也是各行各业的深度好文。如果你有兴趣，赶紧下载博客APP瞧瞧吧。</span></b></u></span>\r\n</p>\r\n<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n	<span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\"><u><span><br />\r\n</span></u></span>\r\n</p>\r\n<p align=\"justify\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:justify;\">\r\n	<span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\"><span>&nbsp;</span></span><span style=\"line-height:21px;color:#464646;font-family:微软雅黑;font-size:10.5pt;\">&nbsp;</span>\r\n</p>\r\n<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n	<span><b><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">1.</span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">《每个人的青春，都有一条弯路》</span></b></span>\r\n</p>\r\n<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n	<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\"><b><span>作者：</span>@奕欢君</b></span></span>\r\n</p>\r\n<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n	<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\"><b>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">我们总说，青春是道明媚的忧伤。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">每个人的青春都有一条弯路。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">有人可能没有摆脱过这种迷茫和孤独，也从来没有摆脱过这种无力感。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">忙碌生活的压迫感、一个人生活太久的孤独感。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">久而久之，我们自己也忘记，生活的动力到底是什么？</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">还好，生活中的那些小确幸，让我们感到一丝温暖。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">这部书，有伤感的倾诉、也有发现的生活美好，包括作者的亲身经历，以及一些中肯的建议。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">海明威说：生活总是让我们遍体鳞伤，但到后来，那些受伤的地方一定会变成我们最强壮的地方。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">所以，请相信，未来会很好。</span></span>\r\n	</p>\r\n	<p align=\"justify\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:justify;\">\r\n		<span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\"><span>&nbsp;</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><b><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">2.&nbsp;</span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">《喜欢的小美好》</span></b></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\"><b><span>作者：</span>@阿羊</b></span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<a href=\"http://blog.sina.cn/dpool/blog/newblog/mblog/controllers/articleserial.php?blog_uid=5599084291&amp;class_id=1\" target=\"_blank\">直达链接：《喜欢的小美好》</a>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">总有些时光，要在过去后才会发现，它已深深刻在记忆里。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">青春里，或多或少都有过一段朦胧的感情。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">“单恋是酸的，相恋是甜的；青涩是酸的，成熟是甜的；这一秒是酸的，下一秒是甜的。”</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">那些不敢说出口的暗恋、连牵手都会脸红的年代。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">夕阳染红了墙角，那个地方站着的男生在等我，而我却在他看不到的地方默默凝视。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">那个总说我不够关心组员的学生会组员，其实，我最在意的是你。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">讲述喜欢的小美好，那些掩埋在青春里未说出口的感情，那些年喜欢过的男孩女孩们。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><b><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">3.《风语青春》</span></b></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\"><b><span>作者：</span>@乐一狸</b></span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<a href=\"http://blog.sina.cn/dpool/blog/newblog/mblog/controllers/articleserial.php?blog_uid=1222089760&amp;class_id=4\" target=\"_blank\">直达链接：《风语青春》</a>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">我们这一生，故事静默如雪，撒满大地，融化在时光里。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">70/80/90/00，都是一个又一个时代的划分与见证。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">当我们渐渐老去，那些散落在回忆里的年少时光便显得尤为珍贵起来。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">这本书便从儿时说起，有童年糗事、求学生涯，以及细碎的日常生活。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\"><span>一段又一段絮絮碎语，对生活的感悟，组成了这部</span>“风语青春”。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">那些记忆中山村饭桌上的悠长年味、跌跌撞撞的旅行、年少时羞涩的暗恋情书。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">总会有某个瞬间，击中你柔软的心事。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\"><span>&nbsp;</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><b><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">4.《没有伞的孩子，要用力奔跑》</span></b></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\"><b><span>作者：</span>@元苑</b></span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<a href=\"http://blog.sina.cn/dpool/blog/newblog/mblog/controllers/articleserial.php?blog_uid=5756846984&amp;class_id=2\" target=\"_blank\">直达链接：《没有伞的孩子，要用力奔跑》</a>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">我想，每个人都曾在脑海里构想过。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">“长大后，我要成为怎样的人？”</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">“长大后，我会拥有什么？我的身边有谁陪着？”</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">也曾有过非常多美好的幻想与建设。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">但生活中总有这样那样的遗憾和不如意。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\"><span>最懂你的朋友离开了，你最爱的恋人也离开了，错过了心仪的工作，心爱的包包烂了</span>……</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">想过放弃，想过离开，想过一了百了。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">但最后还是选择向前奔跑。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">这是一本青年人关于梦想与成长的连载集，有鸡汤、有故事，但更多的是对新生活的一种向上态度。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">如果你也处在二字当头，一个不被认可却有很多梦想的年纪。这本书一定会给你很多启示。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">我们一边行走，一边成长，也许有很多人，活成了自己曾经最不喜欢的模样。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">但是请记得，纵然如此，你也要爱这个世界，任何时候，活着，都是最好的选择。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">而活着这件事，本身就值得热泪盈眶。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\"><span>&nbsp;</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><b><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">5.《水木锦年》</span></b></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\"><b><span>作者：</span>@赤道蚂蚁</b></span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<a href=\"http://blog.sina.cn/dpool/blog/newblog/mblog/controllers/articleserial.php?blog_uid=1657014247&amp;class_id=9\" target=\"_blank\">直达链接：《水木锦年》</a>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">故事的开始是毕业季，故事结束是无限期。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">关于青春，时间以故事的形式，记录下了一段生活的传奇。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">从毕业到工作到组成家庭，这样一段时光，是在年老时，也会微笑着回忆的吧。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">娓娓道来的青春故事，亲切朴实的生活随笔，突然感伤的心情日记。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">有时候，一件毛衣、一首歌、一句简单的老友问候，都会勾起自己无限的回忆。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">这，也是青春最美好的样子。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\"><span>&nbsp;</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><b><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">6.《我想把时光邮寄给你 》</span></b></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\"><b><span>作者：</span>@流年藏忆</b></span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<a href=\"http://blog.sina.cn/dpool/blog/newblog/mblog/controllers/articleserial.php?blog_uid=3264341064&amp;class_id=4\" target=\"_blank\"><span>直达链接：《我想把时光邮寄给你</span>&nbsp;<span>》</span></a>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">&nbsp;<span>在多年以后的某一瞬间，我们的脑海里会突然间想起，大学时光里的那些人那些事儿。</span></span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">青春里的兵荒马乱里，不过就是有聚有散。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">不管最后是怎样的，所有的遇见都是美好的，所以的经历都是有意义的。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">大学的第一天，一定是从室友相识开始的。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">军训、社团、校园恋爱、聚餐，这些事情好像就在昨天发生一般，从我们眼前掠过。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">那段难以忘怀的日子那么闪亮和精彩，友情，爱情，所有的情意，都已经印在心底。</span></span>\r\n	</p>\r\n	<p align=\"center\" style=\"color:#555555;font-family:&quot;font-size:14px;background-color:#FFFFFF;text-align:center;\">\r\n		<span><span style=\"line-height:21px;font-family:微软雅黑;font-size:10.5pt;\">你的青春，也长这样吗？</span></span>\r\n	</p>\r\n<br />\r\n</b></span></span>\r\n</p>','2019-01-02',3,NULL,0);

/*Table structure for table `comment` */

DROP TABLE IF EXISTS `comment`;

CREATE TABLE `comment` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `user_id` int(11) DEFAULT NULL,
  `blog_id` int(11) DEFAULT NULL,
  `comment` varchar(255) DEFAULT NULL,
  `c_date` date DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `FK_Reference_1` (`user_id`),
  KEY `FK_Reference_2` (`blog_id`),
  CONSTRAINT `FK_Reference_1` FOREIGN KEY (`user_id`) REFERENCES `user` (`user_id`),
  CONSTRAINT `FK_Reference_2` FOREIGN KEY (`blog_id`) REFERENCES `blog` (`blog_id`)
) ENGINE=InnoDB AUTO_INCREMENT=18 DEFAULT CHARSET=utf8;

/*Data for the table `comment` */

insert  into `comment`(`id`,`user_id`,`blog_id`,`comment`,`c_date`) values (1,1,1,'1111','2018-12-31'),(2,1,1,'111','2018-12-31'),(3,1,1,'11111111111111','2018-12-31'),(4,1,1,'1111111111111111','2018-12-31'),(5,1,29,'haohaohao号','2019-01-02'),(6,1,27,'苹果垃圾','2019-01-02'),(7,1,27,'贫雇工减肥辣椒粉','2019-01-02'),(8,1,27,'贫雇工减肥辣椒粉','2019-01-02'),(9,1,27,'贫雇工减肥辣椒粉','2019-01-02'),(10,1,37,'即便是作为当代人最大型社交场所之一的微信朋友圈，也在不知不觉间“变了味”：一条社交朋友圈发出之后，给你点赞的人里有1/3没见过面；那些刚用微信时记录下的陈年旧事，伴随着不带滤镜美颜的照片一起，都被你锁进了茫茫的信息之海，嫌麻烦的人超前一步，早早设置了“三天可见”，从此过上加好友再无隐忧的舒适生活。','2019-01-03'),(11,1,37,'即便是作为当代人最大型社交场所之一的微信朋友圈，也在不知不觉间“变了味”：一条社交朋友圈发出之后，给你点赞的人里有1/3没见过面；那些刚用微信时记录下的陈年旧事，伴随着不带滤镜美颜的照片一起，都被你锁进了茫茫的信息之海，嫌麻烦的人超前一步，早早设置了“三天可见”，从此过上加好友再无隐忧的舒适生活。','2019-01-03'),(12,1,37,'与其虔诚地祈求看不见的上帝和菩萨，当代唯物主义青年们早已在现实世界中找到了自己的精神依托：定期转发自带好运光芒的王校长、超越妹妹和信小呆显然已经无法满足“锦鲤信徒们”了，新晋的各种人格、运势、星盘测试正在风靡朋友圈，随便滑动手机屏幕，你会恍若置身一个“大型算命现场”......','2019-01-03'),(13,1,37,'谁知有一天，你在不小心围观到的一场网络论战中发现：乱用名言什么的已经过时了，引用中小学语文课本中的诗词也算好的，终极大招是对方甩手就是一句：“某奇葩说辩手XXX说过@#%^&......”徒留你在原地目瞪口呆','2019-01-03'),(14,1,37,'谁知有一天，你在不小心围观到的一场网络论战中发现：乱用名言什么的已经过时了，引用中小学语文课本中的诗词也算好的，终极大招是对方甩手就是一句：“某奇葩说辩手XXX说过@#%^&......”徒留你在原地目瞪口呆','2019-01-03'),(15,1,37,'谁知有一天，你在不小心围观到的一场网络论战中发现：乱用名言什么的已经过时了，引用中小学语文课本中的诗词也算好的，终极大招是对方甩手就是一句：“某奇葩说辩手XXX说过@#%^&......”徒留你在原地目瞪口呆','2019-01-03'),(16,6,29,'自南宋以后，世界的中心开始西移了。在此后的五百年间，西方经过地理大发现，文艺复兴和工业革命的洗礼与快速发展很快的超过了我们数千年积累下来的文明和财富。回望历史，也许我们真的感受颇深，可是真的只有痛定思痛、做出改变、付诸行动，才能真正实现国富民强。而不是要么醉生梦死的还是关起门来过小日子，要么叫嚣着什么梦什么梦实际是一片泡沫繁荣。因为历史发展的旋涡往往身处当中的我们在当时是看不清的，但是等到时过境迁，也许就不仅仅是改朝换代了','2019-01-03'),(17,5,29,'但是，最后大宋还是悲剧了，为什么？因为养兵的钱都白花了！别说宋军轻武就没有战斗力，主要是宋徽宗战略决策失误啊！联金灭辽，最后顺带把自己也灭了。','2019-01-03');

